import {React, useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setModal} from '../slice/counterSlice';



const Navbar = (props) => {
  const [text1, settext] = useState('Giriş');
  const modal = useSelector((state) => state.modal.value);
  const text = useSelector((state) => state.text.login);
  const dispatch = useDispatch();
  
 const handleClick = ()=> {

dispatch(settext());


}

 
 
 
  return (
    <>
    <div className='bg-yellow-500 flex flex-row justify-around w-full'>
      <div className='flex items-center'>
        <h1 className='text-white font-bold text-2xl font-serif'>OnlineEdu</h1>
      </div>
      <div className='flex flex-row justify-between w-2/4 text-white h-14 items-center text-xl font-semibold'>
        <h1>Anasayfa</h1>
        <h1>Canlı Dersler</h1>
        <h1>Öğretmenlerimiz</h1>
        <h1>İletişim</h1>
        
         <a className='flex items-center flex-row justify-between' onClick={handleClick} href='#'>{text1}  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}>
          <path strokeLinecap="round" strokeLinejoin="round" d="M11 16l-4-4m0 0l4-4m-4 4h14m-5 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1" />
        </svg></a>  

      </div>
      
    </div>
    
    

    </>
  )
}
export default Navbar
