import {React, useState} from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setModal } from '../slice/counterSlice';


const Navbar = (props) => {
  const [isTrue, setisTrue] = useState(false);
  const [email, setemail] = useState('');
  const [password, setpassword] = useState('');
  const [info, setinfo] = useState([]);
  const [modal, setmodal] = useState(false);
  const count = useSelector((state) => state.modal.value);
  const dispatch = useDispatch();
  
  
  

  
  const handleLogin = () => {
    
    dispatch(setModal());
    setisTrue(true);
  }
  const handleCloseModal = () => {
    setisTrue(false);
  }
  const handleChange = (event)=> {
    if (event.target.name === 'email') {
        setemail(event.target.value)
    } else if(event.target.name === 'password'){
      setpassword(event.target.value)
    }
    
  }
  const handleSubmit = (event)=> {
    event.preventDefault();
    if(email !== '' && password !== ''){
      info.push({email, password});
    console.log(info);
    }else{
      setmodal(true);
      setisTrue(false);
    }
    
    
  }
 
  return (
    <>
    <div className='bg-yellow-500 flex flex-row justify-around w-full'>
      <div className='flex items-center'>
        <h1 className='text-white font-bold text-2xl font-serif'>OnlineEdu</h1>
      </div>
      <div className='flex flex-row justify-between w-2/4 text-white h-14 items-center text-xl font-semibold'>
        <h1>Anasayfa</h1>
        <h1>Canlı Dersler</h1>
        <h1>Öğretmenlerimiz</h1>
        <h1>İletişim</h1>
        <h1>{count}</h1>
         <a className='flex items-center flex-row justify-between' href='#' onClick={handleLogin} >Giriş  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth={2}>
          <path strokeLinecap="round" strokeLinejoin="round" d="M11 16l-4-4m0 0l4-4m-4 4h14m-5 4v1a3 3 0 01-3 3H6a3 3 0 01-3-3V7a3 3 0 013-3h7a3 3 0 013 3v1" />
        </svg></a>  

      </div>
      
    </div>
    
    {isTrue === true?
 


<>


 

 <div id="authentication-modal" tabIndex="-1" className="overflow-y-auto overflow-x-hidden fixed top-0 right-0 left-0 z-50 w-full md:inset-0 h-modal md:h-full justify-center items-center flex" aria-modal="true" role="dialog">
     <div className="relative p-4 w-full max-w-md h-full md:h-auto">
      
         <div className="relative bg-white rounded-lg shadow dark:bg-gray-700">
             <button onClick={handleCloseModal} type="button" className="absolute top-3 right-2.5 text-gray-400 bg-transparent hover:bg-gray-200 hover:text-gray-900 rounded-lg text-sm p-1.5 ml-auto inline-flex items-center dark:hover:bg-gray-800 dark:hover:text-white" data-modal-toggle="authentication-modal">
                 <svg aria-hidden="true" className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fillRule="evenodd" d="M4.293 4.293a1 1 0 011.414 0L10 8.586l4.293-4.293a1 1 0 111.414 1.414L11.414 10l4.293 4.293a1 1 0 01-1.414 1.414L10 11.414l-4.293 4.293a1 1 0 01-1.414-1.414L8.586 10 4.293 5.707a1 1 0 010-1.414z" clipRule="evenodd"></path></svg>
                 <span className="sr-only">Close modal</span>
             </button>
             <div className="py-6 px-6 lg:px-8">
                 <h3 className="mb-4 text-xl font-medium text-gray-900 dark:text-white">Sign in to our platform</h3>
                 <form onChange={handleChange} className="space-y-6" action="#">
                     <div>
                         <label htmlFor="email" className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Your email</label>
                         <input type="email" name="email" id="email" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" placeholder="name@company.com" required=""/>
                     </div>
                     <div>
                         <label htmlFor="password" className="block mb-2 text-sm font-medium text-gray-900 dark:text-gray-300">Your password</label>
                         <input type="password" name="password" id="password" placeholder="••••••••" className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-600 dark:border-gray-500 dark:placeholder-gray-400 dark:text-white" required=""/>
                     </div>
                     <div className="flex justify-between">
                         <div className="flex items-start">
                             <div className="flex items-center h-5">
                                 <input id="remember" type="checkbox" value="" className="w-4 h-4 bg-gray-50 rounded border border-gray-300 focus:ring-3 focus:ring-blue-300 dark:bg-gray-600 dark:border-gray-500 dark:focus:ring-blue-600 dark:ring-offset-gray-800" required=""/>
                             </div>
                             <label htmlFor="remember" className="ml-2 text-sm font-medium text-gray-900 dark:text-gray-300">Remember me</label>
                         </div>
                         <a href="#" className="text-sm text-blue-700 hover:underline dark:text-blue-500">Lost Password?</a>
                     </div>
                     <button onClick={handleSubmit} type="submit" className="w-full text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Login to your account</button>
                     <div className="text-sm font-medium text-gray-500 dark:text-gray-300">
                         Not registered? <a href="#" className="text-blue-700 hover:underline dark:text-blue-500">Create account</a>
                     </div>
                 </form>
             </div>
         </div>
     </div>
 </div> 
 </>
: false}
{
      modal === true ? <>
        <div class="bg-red-600 shadow-lg mx-auto w-96 max-w-full text-sm pointer-events-auto bg-clip-padding rounded-lg block mb-3" id="static-example" role="alert" aria-live="assertive" aria-atomic="true" data-mdb-autohide="false">
    <div class="bg-red-600 flex justify-between items-center py-2 px-3 bg-clip-padding border-b border-red-500 rounded-t-lg">
