import { React , useEffect, useState} from 'react';


const Lessons = () => {
const [products, setproducts] = useState([])
    useEffect(() => {
        fetch('https://fakestoreapi.com/products?limit=5')
            .then(res=>res.json())
            .then(json=>{
                console.log(json);
                setproducts(json);
            
            })
    }, [])


  return (
    <div className='flex flex-grow-0'>
        {products ? products.map((item) => (
            <div class="flex justify-center">
            <div class="flex flex-col md:flex-row md:max-w-xl rounded-lg bg-white shadow-lg">
              <img class=" w-full h-96 md:h-auto object-cover md:w-48 rounded-t-lg md:rounded-none md:rounded-l-lg" src="https://mdbootstrap.com/wp-content/uploads/2020/06/vertical.jpg" alt="" />
              <div class="p-6 flex flex-col justify-start">
                <h5 class="text-gray-900 text-xl font-medium mb-2">{item.category}</h5>
                <p class="text-gray-700 text-base mb-4">
                  {item.description}
                </p>
                <p class="text-gray-600 text-xs">Price : {item.price} $</p>
              </div>
            </div>
          </div>
        )) : 'Loading'}
    </div>
  )
}

export default Lessons
