import {createSlice} from '@reduxjs/toolkit';

export const textSlice = createSlice({
      name: 'text',
        initialState: {
          value : 'Giriş',
        },
        reducers: {
          setText: (text) => {
            text.value = 'Çıkış';
          }
        }
      
})
export const { setText } = textSlice.actions;
export default textSlice.reducer
